import React, { useEffect, useState } from 'react'

import CalendarEntry from './components/CalendarEntry'
import AddCalendarEntryForm from './components/AddCalendarEntryForm'

import calendarService from './services/calendarService'

const App = () => {
  const [calendarEntries, setCalendarEntries] = useState([])

  useEffect(() => {
    calendarService
      .getAll()
      .then(calendar => {
        //console.log(calendar)
        setCalendarEntries(calendar)
      })
  }, [])

  const createCalendarEntry = (event) => {
    event.preventDefault()

    if (validateInput()){
      const newEntry = {
        date: date,
        time: time,
        content: content
      }

      console.log(newEntry)

      calendarService
        .addNew(newEntry)
        .then(response => {
          setCalendarentries(calendarEntries.map(c => c.id !== response.data.id ? c : response.data))
          setDate('')
          setTime('')
          setContent('')
        })
    } else {
      window.alert('invalid input')
    }


  }

  const validateInput = () => {
    if (date.match(/^(0?[1-9]|[12][0-9]|3[01])[\/\-](0?[1-9]|1[012])[\/\-]\d{4}$/)
      && time.match(/^[0-2][0-3]:[0-5][0-9]$/)
        ){
          return true
        } else {
          console.log(date.match(/^(0?[1-9]|[12][0-9]|3[01])[\/\-](0?[1-9]|1[012])[\/\-]\d{4}$/))
          return false
        }
  }

  console.log('entries:')
  console.log(calendarEntries)

  return (
    <div>
      <h1>Muista!</h1>
      <ul>
        {calendarEntries.map(c =>
          <CalendarEntry key={c.id} date={c.date} time = {c.time} content={c.content}/>
          )}
      </ul>
      <AddCalendarEntryForm  createCalendarEntry = {createCalendarEntry}/>
      <p>t bob</p>
    </div>
  );
}

export default App
