import React, { useEffect, useState } from 'react'

import CalendarEntry from './components/CalendarEntry'

import calendarService from './services/calendarService'

const App = () => {
  const [calendarEntries, setCalendarEntries] = useState([])
  const [date, setDate] = useState('')
  const [time, setTime] = useState('')
  const [content, setContent] = useState('')

  useEffect(() => {
    calendarService
      .getAll()
      .then(calendar => {
        //console.log(calendar)
        setCalendarEntries(calendar)
      })
  }, [])

  const createCalendarEntry = (event) => {
    event.preventDefault()

    if (validateInput()){
      const newEntry = {
        date: date,
        time: time,
        content: content
      }

      console.log(newEntry)

      calendarService
        .addNew(newEntry)
        .then(response => {
          console.log('lol:')
          console.log(response)
          setCalendarEntries(calendarEntries.concat(response))
          setDate('')
          setTime('')
          setContent('')
        })
    } else {
      window.alert('invalid input')
    }


  }

  const validateInput = () => {
    if (date.match(/^(0?[1-9]|[12][0-9]|3[01])[\/\-](0?[1-9]|1[012])[\/\-]\d{4}$/)
      && time.match(/^[0-2][0-3]:[0-5][0-9]$/)
        ){
          return true
        } else {
          console.log(date.match(/^(0?[1-9]|[12][0-9]|3[01])[\/\-](0?[1-9]|1[012])[\/\-]\d{4}$/)==true)
          return false
        }
  }



  return (
    <div>
      <h1>Muista!</h1>
      <ul>
        {calendarEntries.map(c =>
          <CalendarEntry key={c.id} date={c.date} time = {c.time} content={c.content}/>
          )}
      </ul>
      <form onSubmit={createCalendarEntry}>
      <input
        value={date}
        onChange={(event) => setDate(event.target.value)}
      /> 
      <input 
        value={time}
        onChange={(event) => setTime(event.target.value)}
      />
      <input
        value={content}
        onChange={(event) => setContent(event.target.value)}
      />
      <button type='submit'>lisää</button>
    </form>
      <p>t bob</p>
    </div>
  );
}

export default App
