import React, { useState } from 'react'
import calendarService from '../services/calendarService'

const AddCalendarEntryForm = () => {
  const [date, setDate] = useState('')
  const [time, setTime] = useState('')
  const [content, setContent] = useState('')

  const createCalendarEntry = (event) => {
    event.preventDefault()
    const newEntry = {
      date: date,
      time: time,
      content: content
    }

    calendarService
      .addNew(newEntry)
      .catch(window.alert('unsuccessful'))
  }

  return(
    <form onSubmit={createCalendarEntry}>
      <input 
        onChange={() => setDate(target.value)}
      /> 
      <input 
        onChange={() => setTime(target.value)}
      />
      <input
        onChange={() => setContent(target.value)}
      />
      <button type='submit'>lisää</button>
    </form>
  )
}

export default AddCalendarEntryForm
