import React, { useState } from 'react'
import calendarService from '../services/calendarService'

const AddCalendarEntryForm = ({ calendarEntries, setCalendarentries }) => {
  const [date, setDate] = useState('')
  const [time, setTime] = useState('')
  const [content, setContent] = useState('')

  const createCalendarEntry = (event) => {
    event.preventDefault()

    if (validateInput()){
      const newEntry = {
        date: date,
        time: time,
        content: content
      }

      console.log(newEntry)

      calendarService
        .addNew(newEntry)
        .then(response => {
          setCalendarentries(calendarEntries.map(c => c.id !== response.data.id ? c : response.data))
          setDate('')
          setTime('')
          setContent('')
        })
        .catch(e => {
          window.alert('unsuccessful')
        })
    } else {
      window.alert('invalid input')
    }


  }

  const validateInput = () => {
    if (date.match(/^(0?[1-9]|[12][0-9]|3[01])[\/\-](0?[1-9]|1[012])[\/\-]\d{4}$/)
      && time.match(/^[0-2][0-3]:[0-5][0-9]$/)
        ){
          return true
        } else {
          console.log(date.match(/^(0?[1-9]|[12][0-9]|3[01])[\/\-](0?[1-9]|1[012])[\/\-]\d{4}$/))
          return false
        }
  }

  return(
    <form onSubmit={createCalendarEntry}>
      <input
        onChange={(event) => setDate(event.target.value)}
      /> 
      <input 
        onChange={(event) => setTime(event.target.value)}
      />
      <input
        onChange={(event) => setContent(event.target.value)}
      />
      <button type='submit'>lisää</button>
    </form>
  )
}

export default AddCalendarEntryForm
